import React, { useState } from "react";
import axios from "axios";
import Button from "../../UI/Button/Button";
import "./CourseInput.css";

const CourseInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredComment, setEnteredComment] = useState("");

  const commentSubmitHandler = async (event) => {
    event.preventDefault();
    try {
      await axios.post("https://jsonplaceholder.typicode.com/posts", {
        enteredName,
        enteredComment,
      });
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <div className="spirit__heading">
        <h1>TAMBAH KOMENTAR</h1>
      </div>
      <form onSubmit={commentSubmitHandler} className="spirit-form">
        <div className="field">
          <label className="label">Nama Olahraga</label>
          <div className="control">
            <input
              type="text"
              className="input"
              value={sport_name}
              onChange={(e) => set_sport_name(e.target.value)}
              placeholder="Nama Olahraga"
            />
          </div>
        </div>
        <div className="field">
          <label className="label">Departemen 1</label>
          <div className="control">
            <input
              type="text"
              className="input"
              value={dept_name1}
              onChange={(e) => set_dept_name1(e.target.value)}
              placeholder="Departemen 1"
            />
          </div>
        </div>
        <div>
          <div className="control">
            <button type="submit" className="spirit__add-button">
              TAMBAH
            </button>
          </div>
        </div>
      </form>
    </div>
  );
};

export default CourseInput;
